    console.log(title);
    // this.setState({title: title});
    this.setState({title: e.target.value});

  }

  render(){
    return (
        <div>
            yes it is correct
            <GroupSection state={this.state} />
            <RosterSection />
        </div>
    );
  }
}

export default App;
