import e from 'cors';
import React, { useEffect, useState } from 'react';
import './GroupCard.css';
// import GroupListItem from '../GroupListItem/GroupListItem';

function GroupCard(props) {
    let [showTitle, setShowTitle] = useState(true);
    let [title, setTitle] = useState(props.title);

    useEffect(() => {
        console.log("GROUP CARD mounted");
    })

    let handleChange = e => {
        console.log(e.target.value);
        // props.updateGroupTitle(e, props.index);
        setTitle(e.target.value);
    }

    let toggleTitle = (e) => {
        console.log("Toggling Title:", title);
        props.updateGroupTitle(e, props.index);

        if (showTitle) {
            console.log("T -> F");
            setShowTitle(false);
        } else {
            console.log("F -> T");
            setShowTitle(true);
        };
    }


    return(
        <div className="group-container">
            <div className="group-card--header">
                { showTitle ?
                    <div className="group-title" onClick={toggleTitle}>{title}</div>
                    :
                    <form className="input-wrapper" >
                        <label>
                            <input id="project-title--input" type="text" name="name" placeholder={title} onChange={handleChange} value={props.title} />
                        </label>
                        <input className="title-submit-button" onClick={toggleTitle} type="submit" value="âœ“" />
                    </form>
                }

                <button className="group-del-btn" onClick={props.deleteGroup} value={props.index}>x</button>
            </div>
            <div className="group-items--display">
                {/* Render Groups for ResultPage */}
                {/* <ResultItem entry={entries[0]} /> */}
                {/* {this.state.entries.map((entry) => 
                    <GroupListItem entry={entry} />
                    // <GroupListItem>{entry}</GroupListItem>
                )} */}
            </div>
            {/* <div className="group-del-btn2">x</div> */}
        </div>
    );
}

export default GroupCard;
