import React, { Component } from 'react';
import './TitleBar.css';

class TitleBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      titled: false,
      title: this.props.title,
      // setTitle: this.props.setTitle
    }
    this.toggleTitle = this.toggleTitle.bind(this);
    this.setTitle = this.setTitle.bind(this);
  }

  toggleTitle() {
    if (this.state.title) {
      this.setState({title: false})
      } else {
        this.setState({title: true})
      };
  }

  setTitle() {
    console.log("Setting Title to", e.target.value);
    let title = e.target.value;
    this.setState({title: title});
  }

  // setTitle = this.props.setTitle;

  render() {
    return (
            <section className="titlebar project-title-container">
              <h2>{this.state.title}</h2>
              { this.state.title ?
                <h2>{this.state.title}</h2> :
                <div className="input-wrapper">
                  <input id="project-title--input" type="text" placeholder="ADD PROJECT TITLE" />
                  <div className="title-submit-button" onClick={this.props.setTitle}></div>
                </div>
              }
            </section>
    );
  }
}
export default TitleBar;
