import React, { useState } from 'react';
import './TitleBar.css';


function TitleBar(props) {
  let [showTitle, setShowTitle] = useState(true);
  let [tempTitle, setTempTitle] = useState(props.title);

  let handleChange = e => {
    console.log('Handling Change: ' + e.target.value);
    setTempTitle(e.target.value);
  }
  // let handleChange = e => {
  //   console.log('Handling Change: ' + e.target.value);
  //   props.updateTitle(e.target.value);
  // }



  let toggleTitle = () => {
    console.log("Toggling Title" );
    props.updateTitle(tempTitle);

    if (showTitle) {
      console.log("T -> F");
      setShowTitle(false);
    } else {
      console.log("F -> T");
      setShowTitle(true);
    };
  }

  return (
          <section className="titlebar project-title-container">
            <h3>Project Name:</h3>

            { showTitle ?
              <h2 className="title-display" onClick={toggleTitle} >{tempTitle}</h2> 
              // <h2 className="title-display" onClick={toggleTitle} >{props.title}</h2> 
              : 
              <form className="input-wrapper">
              {/* <form className="input-wrapper" onSubmit={props.handleSubmit}> */}
                <label>
                  <input id="project-title--input" type="text" name="name" placeholder={tempTitle} onChange={handleChange} value={tempTitle} />  
                  {/* <input id="project-title--input" type="text" name="name" placeholder={props.title} onChange={handleChange} value={props.title} />   */}
                </label>
                <input className="title-submit-button" onClick={toggleTitle} type="submit" value="âœ“" />
              </form>
            }
          </section>
  );
}

export default TitleBar;
