import RosterSection from '../RosterSection/RosterSection';
import Footer from '../Footer/Footer';



function InputPage () {
    this.state = {
      GroupObject: this.props.location.state.GroupObject,
    }

  let shuffleData = () => {
    console.log("SHUFFLING...");
    // Shuffle Roster Items randomly
    // Assign each roster item a group #
    let projectName = this.state.projectName;
    console.log(projectName);
  }


  // setProjectTitle(e) {
  //   console.log("Setting new title >>>", e.target.value);
  //   // this.setState({projectName: e.target.value});
  // }



    return (
        <div>
          <header className="App-header">
            <Nav />
            <TitleBar title={this.state.GroupObject.projectName} /> 

          </header>
          <main id="entry-container">
            <GroupSection 
              GroupObject={this.state.GroupObject}
              // addGroup={this.state.GroupObject.addGroupNames} 
              />
            <RosterSection />
          </main>
          <Footer 
            state={this.props.state}
            value="Shuffle" 
            text="Shuffle"
            onClick={this.shuffleData} />
        </div>
    );
  }

export default InputPage;
