import React, { Component } from 'react';
import './TitleBar.css';

class TitleBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // title : this.props.title,
      showTitle: true,
      // setTitle: this.props.setTitle
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.toggleTitle = this.toggleTitle.bind(this);
  }

  handleChange(event) {
    this.setState({title: event.target.value});
  }

  handleSubmit(event) {
    console.log('Submit title: ' + this.state.title);
    event.preventDefault();
    // this.props.setTitle(event.target.value);

    this.toggleTitle();
  }

  toggleTitle() {
    console.log("Toggling Title" );
    if (this.state.showTitle) {
        console.log("T -> F");
        this.setState({showTitle: false})
      } else {
        console.log("F -> T");
        this.setState({showTitle: true})
      };
  }

  render() {
    // console.log("SHOWTITLE?", this.state.showTitle)
    return (
            <section className="titlebar project-title-container">
              <h3>Project Name:</h3>

              { this.state.showTitle ?
                <h2 className="title-display" onClick={this.toggleTitle} onChange={this.handleChange}>{this.props.title}</h2> 
                :
                <form className="input-wrapper" onSubmit={this.handleSubmit}>
                  <label>
                    <input id="project-title--input" type="text" name="name" placeholder={this.state.title} onChange={this.handleChange} value={this.state.title} />
                  </label>
                  <input className="title-submit-button" onClick={this.props.setTitle} type="submit" value="âœ“" />
                </form>
              }
            </section>
    );
  }
}
export default TitleBar;
