//Get to retrieve json

async function getRequest(idNum) {
  let aJson;
  await fetch(`http://localhost:3050/group/${idNum}`)
    .then(res => res.json())
    .then(json => {
        console.log(json);
        aJson = json;
    })
    .catch(e => {
      console.log("Error");
    })
    return aJson;
}

//Post to write to db

function postRequest(json) {
  var raw = JSON.stringify({"projectName":"Project Zoo","groupNames":[{"name":"Section 1"},{"name":"Section 2"}],"persons":[{"name":"Panda","groupNum":1},{"name":"Tiger","groupNum":2}]});
  console.log(json);
  fetch('http://localhost:3050/group/add', {
    method:'POST',
    body: raw
  })
  .then(response => response.text())
  .then(result => console.log(result))
  .catch(error => console.log('error', error));
}



export {getRequest, postRequest}
