import React, { useState } from 'react';
import {Link, Redirect} from 'react-router-dom';
import logo from '../../mallard.svg';
import './LandingPage.css';


function LandingPage() {
  const [ifFetchSuccess, setifFetchSuccess] = useState(true);
  const [idNum, setIdNum] = useState("");
  const [group, setGroup] = useState({
    projectName: "",
    groupNames: [],
    persons: []
  });


  async function getGroup() {
    await fetch(`http://localhost:3050/group/${idNum}`)
      .then(res => res.json())
      .then(json => {
        console.log(json)

      })
      .catch(e => {
        console.log("Error");
      })
  }

  function handleSubmit(event) {
    getGroup();
    event.preventDefault();
  }

  function handleChange(event) {
    const tempIdNum = event.target.value;
    setIdNum(tempIdNum);
    event.preventDefault();
  }

  function redirect(){
    {<Redirect to={{
            pathname: '/results',
            state: {group}
        }}/>}
  }

  return (
    <div className="wrapper">
      <div className="topSection">
        <img src={logo} alt="duck logo" />
      </div>

      <div className="bottomSection">
        <div className="buttonChild">
          <Link to="/grouper">
            <button type="submit" value="Submit" id="btn-start">Create Group</button>
          </Link>
        </div>
        <div className="inputChild">
          <form onSubmit={handleSubmit}>
            <input
              value={idNum}
              onChange={handleChange}
              type="text"
              name="AccessID"
              placeholder="Enter ID"
              className="textField"
              maxLength="7" />
            <button type="submit" name="getResultsPage" className="submit">GO</button>
            {ifFetchSuccess ? redirect(): null}
          </form>
        </div>
      </div>

    </div>
  );
}


export default LandingPage;


