    // props.setTitle
    toggleTitle();
  }

  let toggleTitle = () => {
    console.log("Toggling Title" );
    if (showTitle) {
      console.log("T -> F");
      setShowTitle(false);
      // this.setState({showTitle: false})
    } else {
      console.log("F -> T");
      setShowTitle(true);
      // this.setState({showTitle: true})
    };
  }

  return (
          <section className="titlebar project-title-container">
            <h3>Project Name:</h3>

            { props.showTitle ?
              <h2 className="title-display" onClick={props.toggleTitle} onChange={handleChange}>{props.title}</h2> 
              : 
              <form className="input-wrapper">
              {/* <form className="input-wrapper" onSubmit={props.handleSubmit}> */}
