import React, { useState, useEffect } from 'react';
import './TitleBar.css';


function TitleBar(props) {
  let [showTitle, setShowTitle] = useState(true);
  let [tempTitle, setTempTitle] = useState(props.title);


  // useEffect(()=>{
  //   console.log('useefff');
  // }, tempTitle)

  let handleChange = (event) => {
    console.log('Handling Change: ' + event.target.value);
    // this.setState({title: event.target.value});
    setTempTitle(event.target.value);
  }

  let handleSubmit = (event) => {
    console.log('Submitting title: ' + event.target.value);
    // event.preventDefault();
    this.props.setTitle(tempTitle);
    // props.setTitle
    // this.toggleTitle();
  }

  let toggleTitle = () => {
    console.log("Toggling Title" );
    if (showTitle) {
        console.log("T -> F");
        setShowTitle(false);
        // this.setState({showTitle: false})
      } else {
        console.log("F -> T");
        setShowTitle(true);
        // this.setState({showTitle: true})
      };
  }

  return (
          <section className="titlebar project-title-container">
            <h3>Project Name:</h3>

            { tempTitle ?
              <h2 className="title-display" onClick={toggleTitle} onChange={handleChange}>{tempTitle}</h2> 
              : 
              <form className="input-wrapper" onSubmit={handleSubmit}>
                <label>
                  <input id="project-title--input" type="text" name="name" placeholder={tempTitle} onChange={handleChange} value={tempTitle} />
                </label>
                <input className="title-submit-button" onClick={props.setTitle} type="submit" value="âœ“" />
              </form>
            }
          </section>
  );
}

export default TitleBar;
