import React, { useState, useEffect } from 'react';
import Nav from '../Nav/Nav';
import ResultDisplay from '../ResultDisplay/ResultDisplay';
import Footer from '../Footer/Footer';
import ResultsTitle from '../ResultsTitle/ResultsTitle';
import {Group, Person} from '../../groupClass.js';
import { nanoid } from 'nanoid';

function ResultsPage(props) {
   let GroupObject = new Group();
   const [aGroupObject, setGroupObject] = useState(GroupObject);
   const [fromLandingPage, setFromLandinPage] = useState(props.location.isTrue);
   const [aJson, setAjson] = useState(props.location.json);

   useEffect(() => {
     //setGroupObject(props.location.GroupObject);
     //console.log(aGroupObject.getProjectName());
     console.log(fromLandingPage);
     console.log(props.location.json.projectName);
     if (fromLandingPage) {
       //setFromLandinPage(true);
       setGroupFromCall(aJson);
       //setAjson(props.location.json);
       //console.log(aJson.projectName);
       //console.log(aGroupObject);
       //let tempGroup = aGroupObject;
       console.log(fromLandingPage);
     } else {
       setGroupObject();
     }
   },[]);

   console.log(aJson.projectName);



  function saveProject() {
    console.log("SAVE BUTTON CLICKED...");
    console.log(aJson.projectName);
    console.log(fromLandingPage);

  }

   function setGroupFromCall(aJson){
     let tempGroup = aGroupObject;
     tempGroup.persons = aJson.persons;
     tempGroup.groupNames = aJson.groupNames;
     //tempGroup.projectName = aJson.projectName;
     tempGroup.setProjectName(aJson.projectName);
     setGroupObject(tempGroup);
     console.log(aGroupObject);
   }



  return (
      <div className="page-wrapper">
        <header>
          <Nav />
          <ResultsTitle key={nanoid()} title={aGroupObject.projectName}/>
        </header>
        <main id="entry-container">
          <ResultDisplay />
        </main>
          <Footer
            value="Save"
            text="Save"
            onClick={saveProject}/>
      </div>
  );

}

export default ResultsPage;


