import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import logo from '../../mallard.svg';
import './LandingPage.css';
import Class from '../../groupClass';

function LandingPage() {
  const [idNum, setIdNum] = useState("");
  const [group, setGroup] = useState(new Class());


  setGroup(group.setProjectName("Grouper"));
  console.log(group.getProjectName());

  async function getGroup() {
    await fetch(`http://localhost:3050/group/${idNum}`)
      .then(res => res.json())
      .then(json => console.log(json))
      .catch(e => {
        console.log("Error");
      })
  }

  function handleSubmit(event) {
    getGroup();
    event.preventDefault();
  }

  function handleChange(event) {
    const tempIdNum = event.target.value;
    setIdNum(tempIdNum);
    event.preventDefault();
  }

  return (
    <div class="wrapper">
      <div class="topSection">
        <img src={logo} alt="duck logo" />
      </div>

      <div class="bottomSection">
        <div class="buttonChild">
          <Link to="/grouper">
            <button type="submit" value="Submit" id="btn-start">Create Group</button>
          </Link>
        </div>
        <div class="inputChild">
          <form onSubmit={handleSubmit}>
            <input
              value={idNum}
              onChange={handleChange}
              type="text"
              name="AccessID"
              placeholder="Enter ID"
              class="textField"
              maxlength="7" />
            <button type="submit" name="getResultsPage" class="submit">GO</button>
          </form>
        </div>
      </div>

    </div>
  );
}


export default LandingPage;


