//Get to retrieve json

async function getRequest(idNum) {
  let aJson;
  await fetch(`http://localhost:3050/group/${idNum}`)
    .then(res => res.json())
    .then(json => {
        console.log(json);
        aJson = json;
    })
    .catch(e => {
      console.log("Error");
    })
    return aJson;
}

