  var raw = JSON.stringify({"projectName":"Project Zoo","groupNames":[{"name":"Section 1"},{"name":"Section 2"}],"persons":[{"name":"Panda","groupNum":1},{"name":"Tiger","groupNum":2}]});

  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };

  fetch("http://localhost:3050/group/add", requestOptions)
    .then(response => response.text())
    .then(result => console.log(result))
    .catch(error => console.log('error', error));
